    public class SearchResultsSerialisationTests
    {
        [Fact]
        public void Can_serialise_example_json()
        {
            var model = JsonConvert.DeserializeObject<RawSearchResultsRoot>(ExampleJson);

            model.ShouldNotBeNull();
            model.Collection.ShouldNotBeNull();
            model.Collection.Href.ShouldBe("https://images-api.nasa.gov/search?q=planet");
            var firstItem = model.Collection.Items.FirstOrDefault();
            firstItem.ShouldNotBeNull();
            firstItem.Data.First().DateCreated.ShouldBe(DateTime.Parse("2016-01-20T00:00:00Z"));
        }

